%
% MATLAB script for plotting the directivity function for
% a circular, baffled piston
%
% Parameters:
% k
Wavenumber
% rho
Density
% c Speed of Sound
% a Radius of piston of piston
%
clear
figure(1)
hold off
k=10;
rho=1000;
c=1500;
a=1.0;
ka=k*a;
figure(1);
kxm=2*ka;
nkx=300;
dkx=2*kxm/(nkx-1);
x=[-kxm:dkx:kxm];
y=x;
o=ones(1,nkx);
kx=x' * o;
ky=(y' *o)';
kr=abs(complex(kx,ky));
ss=rho*k*c*a^2 * besselj(1,kr)./kr;
%surfc(kx,ky,dba(ss));
wavei(dba(ss)',x,y)
shading('flat')
axis('equal')
b=xlabel('k_x a')
set(b,'FontSize',16);
b=ylabel('k_y a')
set(b,'FontSize',16);
tit=['Circular Piston - ka = ' num2str(k*a) ]
b=title(tit);
set(b,'FontSize',20);
nphi=361;
dphi=2*pi/(nphi-1);
phi=[0:dphi:2*pi];
xx=k*a*cos(phi);
yy=k*a*sin(phi);
hold on
b=plot(xx,yy,'b');
set(b,'LineWidth',3);

